"use strict";Vue.component("m-message",{template:'\n    <div class="m-message" @click="open">\n        <slot></slot>\n          <div class="layer" v-if="show">\n            <transition name="scale">\n              <div class="model-box" v-show="showBox">\n                <h3 v-if="title">\n                  {{ title }}\n                </h3>\n                <p>{{ content }}</p>\n                <span class="close-btn" @click="eventHandler(\'cancel\')">\n                  <i class="zmdi zmdi-close"></i>\n                </span>\n                <div v-if="type===\'alert\'">\n                  <span @click="eventHandler(\'cancel\')" class="btn btn-success">\n                    确定\n                  </span>\n                </div>\n                <div v-if="type===\'confirm\'">\n                  <span @click="eventHandler(\'cancel\')" class="btn btn-default">\n                    取消\n                  </span>\n                  <span @click="eventHandler(\'confirm\')" class="btn btn-success">\n                    确定\n                  </span>\n                </div>\n              </div>\n            </transition>\n          </div>\n    </div>\n  ',props:{type:{type:String,default:"alert"},title:{type:String,default:"标题"},content:{type:String,default:"内容"},btnTxt:{type:String,default:"确定"}},data:function(){return{show:!1,showBox:!1}},methods:{open:function(n){"BUTTON"===n.target.tagName&&(this.show=!0,setTimeout(function(){this.showBox=!0}.bind(this),10),document.body.style.overflow="hidden")},eventHandler:function(n){"cancel"===n&&(this.show=this.showBox=!1,document.body.style.overflow="auto"),"confirm"===n&&(this.show=this.showBox=!1,this.$emit("confirm"),document.body.style.overflow="auto")}}}),new Vue({el:"#app",methods:{msgEvent:function(){console.log("执行操作")}}});